import React, { useState } from 'react';

const App: React.FC = () => {
  const [topic, setTopic] = useState<string>('');
  const [article, setArticle] = useState<any>(null);

  const generateArticle = async () => {
    // Call your backend API to generate an article here
    const response = await fetch(`/generateArticle?topic=${topic}`);
    const data = await response.json();
    setArticle(data);
  };

  return (
    <div className="App">
      <h1>Article Generator</h1>
      <input
        type="text"
        value={topic}
        onChange={(e) => setTopic(e.target.value)}
        placeholder="Enter topic"
      />
      <button onClick={generateArticle}>Generate Article</button>

      {article && (
        <div>
          <h2>{article.headline}</h2>
          <p>{article.intro}</p>
          <p>{article.body}</p>
          <p><strong>Conclusion: </strong>{article.conclusion}</p>
        </div>
      )}
    </div>
  );
}

export default App;
