import { useState } from 'react';
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";
import { Badge } from "@/components/ui/badge";
import { PenTool, Sparkles, FileText, Brain, Loader2 } from 'lucide-react';

type Article = {
  headline: string;
  intro: string;
  body: string;
  conclusion: string;
};

const Index = () => {
  const [topic, setTopic] = useState('');
  const [article, setArticle] = useState<Article | null>(null);
  const [loading, setLoading] = useState(false);

  const generateArticle = async () => {
    setLoading(true);
    
    // محاكاة عملية إنشاء المقال
    setTimeout(() => {
      const mockArticle: Article = {
        headline: `دليل شامل حول ${topic}`,
        intro: `مقدمة تفصيلية حول موضوع ${topic} وأهميته في العصر الحديث. سنستكشف جميع الجوانب المهمة لهذا الموضوع.`,
        body: `يعتبر ${topic} من المواضيع المهمة التي تستحق الدراسة والفهم. في هذا القسم، سنتعمق في التفاصيل ونقدم معلومات قيمة ومفيدة للقارئ. سنتناول الأساسيات والمفاهيم المتقدمة، مع أمثلة عملية وتطبيقات في الحياة الواقعية.`,
        conclusion: `في الختام، يمكننا القول أن ${topic} موضوع مهم يستحق الاهتمام والدراسة. نأمل أن تكون هذه المعلومات مفيدة ومساعدة لك في فهم هذا الموضوع بشكل أفضل.`
      };
      setArticle(mockArticle);
      setLoading(false);
    }, 3000);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && topic.trim() && !loading) {
      generateArticle();
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-purple-50">
      {/* Header Section */}
      <div className="relative overflow-hidden bg-gradient-to-r from-blue-600 via-purple-600 to-indigo-700">
        <div className="absolute inset-0 bg-black/10"></div>
        <div className="relative z-10 container mx-auto px-4 py-16 text-center">
          <div className="flex items-center justify-center mb-6">
            <div className="p-3 bg-white/20 backdrop-blur-sm rounded-full mr-4">
              <Brain className="h-8 w-8 text-white" />
            </div>
            <h1 className="text-4xl md:text-5xl font-bold text-white">
              مولد المقالات بالذكاء الاصطناعي
            </h1>
          </div>
          <p className="text-xl text-white/90 max-w-2xl mx-auto mb-8">
            اكتب موضوعاً واحصل على مقال احترافي مُنشأ بالذكاء الاصطناعي في ثوانٍ معدودة
          </p>
          
          {/* Features badges */}
          <div className="flex flex-wrap justify-center gap-3">
            <Badge variant="secondary" className="bg-white/20 text-white border-white/30">
              <Sparkles className="h-4 w-4 mr-2" />
              محتوى أصلي
            </Badge>
            <Badge variant="secondary" className="bg-white/20 text-white border-white/30">
              <PenTool className="h-4 w-4 mr-2" />
              كتابة احترافية
            </Badge>
            <Badge variant="secondary" className="bg-white/20 text-white border-white/30">
              <FileText className="h-4 w-4 mr-2" />
              بنية منظمة
            </Badge>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="container mx-auto px-4 py-12">
        {/* Input Section */}
        <Card className="max-w-2xl mx-auto mb-12 shadow-xl border-0 bg-white/80 backdrop-blur-sm">
          <CardHeader className="text-center">
            <CardTitle className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
              ابدأ بكتابة موضوعك
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-6">
            <div className="relative">
              <Input
                type="text"
                placeholder="مثال: فوائد الذكاء الاصطناعي في التعليم"
                value={topic}
                onChange={(e) => setTopic(e.target.value)}
                onKeyPress={handleKeyPress}
                className="h-14 text-lg px-6 border-2 border-gray-200 focus:border-blue-500 transition-all duration-300"
                disabled={loading}
              />
            </div>
            
            <Button
              onClick={generateArticle}
              disabled={loading || !topic.trim()}
              className="w-full h-14 text-lg font-semibold bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 transition-all duration-300 transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none"
            >
              {loading ? (
                <>
                  <Loader2 className="mr-2 h-5 w-5 animate-spin" />
                  جاري إنشاء المقال...
                </>
              ) : (
                <>
                  <Sparkles className="mr-2 h-5 w-5" />
                  إنشاء المقال
                </>
              )}
            </Button>
          </CardContent>
        </Card>

        {/* Loading State */}
        {loading && (
          <Card className="max-w-4xl mx-auto mb-8 shadow-lg border-0 bg-white/80 backdrop-blur-sm">
            <CardContent className="p-8">
              <div className="text-center space-y-4">
                <div className="flex justify-center">
                  <div className="p-4 bg-gradient-to-r from-blue-500 to-purple-500 rounded-full">
                    <Brain className="h-8 w-8 text-white animate-pulse" />
                  </div>
                </div>
                <h3 className="text-xl font-semibold text-gray-700">الذكاء الاصطناعي يعمل الآن...</h3>
                <p className="text-gray-600">يتم تحليل الموضوع وإنشاء محتوى احترافي</p>
                <div className="flex justify-center space-x-2">
                  <div className="w-2 h-2 bg-blue-500 rounded-full animate-pulse"></div>
                  <div className="w-2 h-2 bg-purple-500 rounded-full animate-pulse" style={{animationDelay: '0.2s'}}></div>
                  <div className="w-2 h-2 bg-indigo-500 rounded-full animate-pulse" style={{animationDelay: '0.4s'}}></div>
                </div>
              </div>
            </CardContent>
          </Card>
        )}

        {/* Article Display */}
        {article && !loading && (
          <Card className="max-w-4xl mx-auto shadow-xl border-0 bg-white/90 backdrop-blur-sm animate-fade-in">
            <CardHeader className="bg-gradient-to-r from-blue-50 to-purple-50 border-b">
              <div className="flex items-center justify-between">
                <CardTitle className="text-2xl font-bold text-gray-800 flex items-center">
                  <FileText className="h-6 w-6 mr-3 text-blue-600" />
                  المقال المُنشأ
                </CardTitle>
                <Badge className="bg-green-100 text-green-800 border-green-200">
                  <Sparkles className="h-3 w-3 mr-1" />
                  جاهز
                </Badge>
              </div>
            </CardHeader>
            <CardContent className="p-8 space-y-8">
              {/* Headline */}
              <div className="text-center">
                <h2 className="text-3xl md:text-4xl font-bold text-gray-900 leading-tight mb-4">
                  {article.headline}
                </h2>
                <Separator className="w-24 mx-auto bg-gradient-to-r from-blue-500 to-purple-500 h-1 rounded" />
              </div>

              {/* Article Sections */}
              <div className="space-y-8">
                {/* Introduction */}
                <div className="bg-blue-50 p-6 rounded-xl border-l-4 border-blue-500">
                  <h3 className="text-xl font-semibold text-blue-800 mb-3 flex items-center">
                    <div className="w-8 h-8 bg-blue-500 rounded-full flex items-center justify-center mr-3">
                      <span className="text-white font-bold text-sm">1</span>
                    </div>
                    المقدمة
                  </h3>
                  <p className="text-gray-700 leading-relaxed text-lg">
                    {article.intro}
                  </p>
                </div>

                {/* Body */}
                <div className="bg-purple-50 p-6 rounded-xl border-l-4 border-purple-500">
                  <h3 className="text-xl font-semibold text-purple-800 mb-3 flex items-center">
                    <div className="w-8 h-8 bg-purple-500 rounded-full flex items-center justify-center mr-3">
                      <span className="text-white font-bold text-sm">2</span>
                    </div>
                    المحتوى الرئيسي
                  </h3>
                  <p className="text-gray-700 leading-relaxed text-lg">
                    {article.body}
                  </p>
                </div>

                {/* Conclusion */}
                <div className="bg-indigo-50 p-6 rounded-xl border-l-4 border-indigo-500">
                  <h3 className="text-xl font-semibold text-indigo-800 mb-3 flex items-center">
                    <div className="w-8 h-8 bg-indigo-500 rounded-full flex items-center justify-center mr-3">
                      <span className="text-white font-bold text-sm">3</span>
                    </div>
                    الخاتمة
                  </h3>
                  <p className="text-gray-700 leading-relaxed text-lg">
                    {article.conclusion}
                  </p>
                </div>
              </div>

              {/* Action Buttons */}
              <div className="flex flex-col sm:flex-row gap-4 pt-6 border-t">
                <Button
                  onClick={() => setTopic('')}
                  variant="outline"
                  className="flex-1 h-12 border-2 hover:bg-gray-50 transition-all duration-300"
                >
                  <PenTool className="mr-2 h-4 w-4" />
                  إنشاء مقال جديد
                </Button>
                <Button
                  onClick={() => navigator.clipboard.writeText(`${article.headline}\n\n${article.intro}\n\n${article.body}\n\n${article.conclusion}`)}
                  className="flex-1 h-12 bg-gradient-to-r from-green-600 to-emerald-600 hover:from-green-700 hover:to-emerald-700 transition-all duration-300"
                >
                  <FileText className="mr-2 h-4 w-4" />
                  نسخ المقال
                </Button>
              </div>
            </CardContent>
          </Card>
        )}

        {/* Footer Info */}
        {!article && !loading && (
          <div className="max-w-2xl mx-auto text-center mt-12">
            <p className="text-gray-600 leading-relaxed">
              مولد المقالات بالذكاء الاصطناعي يساعدك على إنشاء محتوى عالي الجودة بسرعة وسهولة. 
              فقط اكتب موضوعك واحصل على مقال منظم ومفصل في ثوانٍ معدودة.
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Index;
