import { useState } from 'react';
import './App.css';

type Article = {
  headline: string;
  intro: string;
  body: string;
  conclusion: string;
};

function App() {
  const [topic, setTopic] = useState('');
  const [article, setArticle] = useState<Article | null>(null);
  const [loading, setLoading] = useState(false);
  const [lang, setLang] = useState(navigator.language.startsWith('ar') ? 'ar' : 'en');

  const t = (en: string, ar: string) => (lang === 'ar' ? ar : en);

  const generateArticle = async () => {
    setLoading(true);
    const res = await fetch('http://localhost:5000/generate-article', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ topic }),
    });
    const data = await res.json();
    setArticle(data);
    setLoading(false);
  };

  return (
    <div dir={lang === 'ar' ? 'rtl' : 'ltr'} className="min-h-screen flex items-center justify-center bg-gradient-to-br from-slate-50 to-slate-200 dark:from-slate-800 dark:to-slate-900 transition-all duration-300 font-cairo px-4">
      <div className="glass-container w-full max-w-2xl p-8 rounded-2xl shadow-2xl">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold text-indigo-600 dark:text-indigo-300">{t('AI Article Generator', 'مولّد المقالات بالذكاء الاصطناعي')}</h1>
          <button
            className="text-sm text-indigo-600 hover:underline dark:text-indigo-300"
            onClick={() => setLang(lang === 'ar' ? 'en' : 'ar')}
          >
            🌐 {lang === 'ar' ? 'English' : 'العربية'}
          </button>
        </div>

        <input
          type="text"
          placeholder={t('Enter a topic...', 'أدخل موضوعًا...')}
          value={topic}
          onChange={(e) => setTopic(e.target.value)}
          className="w-full p-3 rounded-md bg-white dark:bg-slate-700 dark:text-white border border-gray-200 dark:border-slate-600 mb-4 shadow-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
        />

        <button
          onClick={generateArticle}
          disabled={loading}
          className="w-full py-3 bg-indigo-600 text-white font-semibold rounded-md hover:bg-indigo-700 disabled:opacity-50 transition duration-300 transform hover:scale-105"
        >
          {loading ? t('Generating...', 'جارٍ التوليد...') : t('Generate', 'توليد')}
        </button>

        {article && (
          <div className="mt-8 bg-white dark:bg-slate-800 p-5 rounded-xl shadow-md space-y-4 text-left">
            <h2 className="text-xl font-bold text-indigo-700 dark:text-indigo-300">{article.headline}</h2>
            <p><strong>{t('Intro:', 'المقدمة:')}</strong> {article.intro}</p>
            <p><strong>{t('Body:', 'المتن:')}</strong> {article.body}</p>
            <p><strong>{t('Conclusion:', 'الخاتمة:')}</strong> {article.conclusion}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
