import { useState } from 'react';
import './App.css';



type Article = {
  headline: string;
  intro: string;
  body: string;
  conclusion: string;
};

function App() {
  const [topic, setTopic] = useState('');
  const [article, setArticle] = useState<Article | null>(null);
  const [loading, setLoading] = useState(false);

  const generateArticle = async () => {
    setLoading(true);
    const res = await fetch('http://localhost:5000/generate-article', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ topic }),
    });
    const data = await res.json();
    setArticle(data);
    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6 flex flex-col items-center justify-center">
      <h1 className="text-3xl font-bold text-blue-600 mb-4">AI Article Generator</h1>
      <div className="flex space-x-2 mb-4">
        <input
          type="text"
          placeholder="Enter a topic"
          value={topic}
          onChange={(e) => setTopic(e.target.value)}
          className="p-3 w-72 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <button
          onClick={generateArticle}
          disabled={loading}
          className="px-6 py-3 bg-blue-600 text-white rounded-lg disabled:bg-gray-400 hover:bg-blue-500 transition duration-300"
        >
          {loading ? 'Generating...' : 'Generate'}
        </button>
      </div>

      {article && (
        <div className="w-full max-w-3xl bg-white p-6 rounded-lg shadow-lg mt-6">
          <h2 className="text-2xl font-semibold text-gray-800 mb-2">{article.headline}</h2>
          <p><strong className="font-semibold">Intro:</strong> {article.intro}</p>
          <p><strong className="font-semibold">Body:</strong> {article.body}</p>
          <p><strong className="font-semibold">Conclusion:</strong> {article.conclusion}</p>
        </div>
      )}
    </div>
  );
}

export default App;
