import { useState } from 'react';
import './App.css';

type Article = {
  headline: string;
  intro: string;
  body: string;
  conclusion: string;
};

function App() {
  const [topic, setTopic] = useState('');
  const [article, setArticle] = useState<Article | null>(null);
  const [loading, setLoading] = useState(false);

  const generateArticle = async () => {
    setLoading(true);
    const res = await fetch('http://localhost:5000/generate-article', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ topic }),
    });
    const data = await res.json();
    setArticle(data);
    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center py-10">
      <div className="bg-white rounded-xl shadow-lg p-8 w-full sm:w-3/4 md:w-1/2 lg:w-1/3">
        <h1 className="text-4xl font-bold text-center text-blue-600 mb-6">AI Article Generator</h1>
        
        <input
          type="text"
          placeholder="Enter a topic"
          value={topic}
          onChange={(e) => setTopic(e.target.value)}
          className="w-full p-3 border-2 border-gray-300 rounded-lg mb-4 text-lg"
        />
        
        <button
          onClick={generateArticle}
          disabled={loading}
          className={`w-full py-3 text-white font-semibold rounded-lg transition duration-300 ${
            loading ? 'bg-gray-400' : 'bg-blue-600 hover:bg-blue-700'
          }`}
        >
          {loading ? 'Generating...' : 'Generate'}
        </button>

        {article && (
          <div className="mt-6">
            <h2 className="text-2xl font-semibold text-gray-800">{article.headline}</h2>
            <p className="mt-2 text-gray-600"><strong>Intro:</strong> {article.intro}</p>
            <p className="mt-2 text-gray-600"><strong>Body:</strong> {article.body}</p>
            <p className="mt-2 text-gray-600"><strong>Conclusion:</strong> {article.conclusion}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
